// src/App.js
import React from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  Grid,
  Paper,
  Container,
  ThemeProvider,
  createTheme,
} from "@mui/material";

const formalTheme = createTheme({
  palette: {
    mode: "dark",
    primary: { main: "#3F51B5" },        // Buttons / highlights
    secondary: { main: "#5C6BC0" },      // Hover highlights
    background: { default: "#1E1E1E", paper: "#2C2C2C" },
    text: { primary: "#E0E0E0", secondary: "#B0B0B0" },
  },
});

function App() {
  const features = [
    { icon: "📄", title: "Summarization", desc: "Upload a PDF and get a concise AI summary." },
    { icon: "✍️", title: "Proofreading", desc: "Check grammar, spelling, and clarity in your text." },
    { icon: "📑", title: "Formatting", desc: "Instantly format drafts in IEEE, APA, ACM, and more." },
    { icon: "🤝", title: "Collaboration", desc: "Add comments and collaborate with your peers." },
  ];

  const aboutText =
    "Proofessor is your AI-powered research assistant. Summarize PDFs, proofread drafts, format according to standards, and collaborate with your team — all in one place.";

  return (
    <ThemeProvider theme={formalTheme}>
      {/* Whole Layout */}
      <Box sx={{ fontFamily: "Roboto, sans-serif" }}>
        {/* Navbar */}
        <AppBar position="sticky" sx={{ boxShadow: 3, backgroundColor: "#1E1E1E" }}>
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1, color: "#E0E0E0" }}>
              Proofessor
            </Typography>
            {["Home", "Features", "About", "Contact"].map((item) => (
              <Button
                key={item}
                sx={{
                  mx: 1,
                  textTransform: "none",
                  color: "#E0E0E0",
                  "&:hover": { backgroundColor: "#5C6BC0", color: "#E0E0E0" },
                }}
              >
                {item}
              </Button>
            ))}
          </Toolbar>
        </AppBar>

        {/* Hero Section */}
        <Box
          sx={{
            textAlign: "center",
            py: { xs: 8, md: 12 },
            px: 2,
            backgroundColor: "#1E1E1E",
          }}
        >
          <Container maxWidth="md">
            <Typography
              variant="h3"
              sx={{
                fontWeight: "bold",
                mb: 3,
                lineHeight: 1.2,
                color: "#3F51B5",
                animation: "fadeIn 1s ease-in-out",
              }}
            >
              AI-Powered Research Assistant
            </Typography>
            <Typography
              variant="h6"
              sx={{ mb: 4, color: "#E0E0E0", fontWeight: 400, animation: "fadeIn 1.5s ease-in-out" }}
            >
              Summarize, Proofread, Format, and Collaborate on Research Papers
            </Typography>
            <Box sx={{ mt: 2, animation: "fadeIn 2s ease-in-out" }}>
              <Button
                variant="contained"
                size="large"
                sx={{
                  mr: 2,
                  backgroundColor: "#3F51B5",
                  color: "#E0E0E0",
                  fontWeight: "bold",
                  "&:hover": { backgroundColor: "#5C6BC0", color: "#E0E0E0" },
                }}
              >
                Get Started
              </Button>
              <Button
                variant="outlined"
                size="large"
                sx={{
                  color: "#3F51B5",
                  borderColor: "#3F51B5",
                  fontWeight: "bold",
                  "&:hover": { backgroundColor: "#5C6BC0", borderColor: "#5C6BC0", color: "#E0E0E0" },
                }}
              >
                Learn More
              </Button>
            </Box>
          </Container>
        </Box>

        {/* Features Section */}
        <Box sx={{ py: 8, backgroundColor: "#1E1E1E" }}>
          <Container maxWidth="lg">
            <Typography variant="h4" align="center" gutterBottom sx={{ mb: 6, color: "#3F51B5" }}>
              Features
            </Typography>
            <Grid container spacing={4}>
              {features.map((f, i) => (
                <Grid item xs={12} md={3} key={i}>
                  <Paper
                    elevation={6}
                    sx={{
                      p: 4,
                      textAlign: "center",
                      transition: "all 0.3s",
                      backgroundColor: "#2C2C2C",
                      "&:hover": {
                        transform: "translateY(-8px)",
                        boxShadow: "0px 8px 20px rgba(0,0,0,0.5)",
                        backgroundColor: "#5C6BC0",
                        color: "#E0E0E0",
                      },
                    }}
                  >
                    <Box sx={{ fontSize: 40, mb: 2 }}>{f.icon}</Box>
                    <Typography variant="h6" gutterBottom color="inherit">
                      {f.title}
                    </Typography>
                    <Typography variant="body2" color="inherit">
                      {f.desc}
                    </Typography>
                  </Paper>
                </Grid>
              ))}
            </Grid>
          </Container>
        </Box>

        {/* About Section */}
        <Box sx={{ py: 8, backgroundColor: "#1E1E1E" }}>
          <Container maxWidth="md">
            <Typography variant="h4" align="center" gutterBottom sx={{ mb: 3, color: "#3F51B5" }}>
              About Proofessor
            </Typography>
            <Typography variant="body1" align="center" sx={{ color: "#E0E0E0", fontSize: "1.1rem" }}>
              {aboutText}
            </Typography>
          </Container>
        </Box>

        {/* Footer */}
        <Box sx={{ py: 4, textAlign: "center", backgroundColor: "#1E1E1E", color: "#E0E0E0" }}>
          <Container maxWidth="md">
            <Typography variant="body1" sx={{ mb: 2 }}>
              © {new Date().getFullYear()} Proofessor. All rights reserved.
            </Typography>
            <Box sx={{ mt: 1 }}>
              {["Twitter", "LinkedIn", "GitHub"].map((s) => (
                <Button
                  key={s}
                  sx={{
                    color: "#3F51B5",
                    mx: 1,
                    "&:hover": { color: "#5C6BC0" },
                  }}
                >
                  {s}
                </Button>
              ))}
            </Box>
          </Container>
        </Box>

        {/* Fade-in keyframes */}
        <style>
          {`
            @keyframes fadeIn {
              from { opacity: 0; transform: translateY(20px); }
              to { opacity: 1; transform: translateY(0); }
            }
          `}
        </style>
      </Box>
    </ThemeProvider>
  );
}

export default App;
